import './FollowingCard.css'
import React, { Component } from 'react'
import FollowersService from '../Services/FollowingService'

class FollowingCard extends Component {
    constructor(props) {
        super(props)

        this.state = {
            followers: []
        }

        this.addFollower = this.addFollower.bind(this);
        this.deleteFollower = this.deleteFollower.bind(this);
    }


    render() {
        return (
            <div className="FollowersCard">
                <h3>Followers</h3>

                { this.state.followers.map(follower => 
                    <div className="follower" key={follower.id}>
                        <div>
                            <img src={follower.img} alt="" className='followerImage' />
                            <div className="name">
                                <span>{follower.fullname}</span>
                            </div>
                        </div>
                        <div>
                            <button onClick={() => this.deleteFollower(follower.id)}>Delete</button>
                            <button onClick={() => this.viewFollower(follower.id)}>View</button>
                        </div>
                    </div>
                )}
            </div>
        )
    }
}

export default FollowingCard
