// actions.js
export const ADD_FOLLOWED_MEMBER = 'ADD_FOLLOWED_MEMBER';
export const REMOVE_FOLLOWED_MEMBER = 'REMOVE_FOLLOWED_MEMBER';

export function addFollowedMember(member) {
  return { type: ADD_FOLLOWED_MEMBER, payload: member };
}

export function removeFollowedMember(memberId) {
  return { type: REMOVE_FOLLOWED_MEMBER, payload: memberId };
}
