import './Login.css';
import NavBar from '../Home/NavBar';
import { Link } from 'react-router-dom';
import logo from '../../images/logo.png';
import { FaEnvelope, FaLock, FaFacebook, FaGoogle, FaApple, FaEyeSlash, FaEye } from 'react-icons/fa';
import { useState } from 'react';

const emailInput = document.querySelector('#email');
const passwordInput = document.querySelector('#password');

const LoginPage = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [isEmailValid, setIsEmailValid] = useState(false);
  const [isPasswordValid, setIsPasswordValid] = useState(false);

  const handleEmailChange = (e) => {
    setEmail(e.target.value);
    setIsEmailValid(e.target.value.includes('@'));
  }

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
    setIsPasswordValid(e.target.value.length >= 6);
  }

  const togglePassword = () => {
    const passwordInput = document.getElementById('password-input');
    if (passwordInput.type === 'password') {
        passwordInput.type = 'text';
    } else {
        passwordInput.type = 'password';
    }
  }
  const handleSubmit = (e) => {
    e.preventDefault();
    if (!emailInput.value) {
      alert('Please enter your email');
      return;
    }

    if (!passwordInput.value) {
      alert('Please enter your password');
      return;
    }

    // Rest of the code
  };

  return (
    <div className='login-page'>
      <div>
        <NavBar />
      </div>
      <div className="login-card-container">
        <div className="login-card">
          <div className="login-card-logo">
            <img src={logo} alt="logo" />
          </div>
          <div className="login-card-header">
            <h1>Sign In</h1>
            <div>Please login to use the platform</div>
          </div>
          <form className="login-card-form" onSubmit={handleSubmit}>
            <div className="form-item">
              <input type="text" placeholder="Enter Email" className={`form-input ${isEmailValid ? 'valid' : ''}`} value={email} onChange={handleEmailChange} />
              <i><FaEnvelope size={'1.3em'} color='#37018e' /></i>
            </div>
            <div className="form-item">
              <FaLock size={'1.3em'} color='#37018e' />
              <input type="password" id='password-input' placeholder="Enter Password" className={`form-input ${isPasswordValid ? 'valid' : ''}`} value={password} onChange={handlePasswordChange} />
              <i onClick={togglePassword}>{passwordInput.type === 'password' ? <FaEyeSlash /> : <FaEye />}</i>
            </div>
            <div className="form-item-other">
              <div className="checkbox">
                <input type="checkbox" id="rememberMeCheckbox" />
                <label htmlFor="rememberMeCheckbox">Remember me</label>
              </div>
              <a href="/" className='login-card-frgt-pwd'>I forgot my password!</a>
            </div>
            <button type="submit" className='login-btn' disabled={!isEmailValid || !isPasswordValid}>Sign In</button>
          </form>
          <div className="login-card-footer">
            Don't have an account? <Link to='/register' className='new-acc'>Create a free account.</Link>
          </div>
        </div>
        <div className="login-card-social">
          <div>Other Sign-In Options</div>
          <div className="login-card-social-btns">
            <a href="/"><FaFacebook size={'2em'} /></a>
            <a href="/"><FaGoogle size={'2em'} /></a>
            <a href="/"><FaApple size={'2em'} /></a>
          </div>
        </div>
      </div>
    </div>
  );

}
export default LoginPage;
