import React from 'react';
import './About.css';
import image from '../images/logo.png';

const About = () => {
  return (
    <div className='about'> 
      <div className='title_about'>
        <h1 className='heading_about'>Learnify</h1>
        <p className='desc'>Learnify is a comprehensive resource hub designed to empower learning for everyone. It offers a wealth of educational resources, including tutorials, articles, videos, and courses, covering a broad range of topics from various fields. Whether you are a student, educator, or simply someone who loves to learn, Learnify has something to offer. Our platform is easy to use, intuitive, and constantly updated with new content to ensure that our users always have access to the latest and most relevant resources. With Learnify, you can take your learning journey to the next level and achieve your goals.</p>
      </div>
      <img src={image} alt='Learnify logo'/>
    </div>
  );
};

export default About;
