import './Login.css';
import NavBar from '../Home/NavBar';
import React, { useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import { setUserSession } from '../Utils/Common'

const LoginPage = (props) => {
  const [loading, setLoading] = useState(false);
  const username = useFormInput('');
  const password = useFormInput('');
  const [error, setError] = useState(null);

  // handle button click of login form
  const handleLogin = () => {
    setError(null);
    setLoading(true);
    axios.post('http://localhost:4000/users/signin', { username: username.value, password: password.value }).then(response => {
      setLoading(false);
      setUserSession(response.data.token, response.data.user);
      props.history.push('/userpage');
    }).catch(error => {
      setLoading(false);
      if (error.response.status === 401) setError(error.response.data.message);
      else setError("Something went wrong. Please try again later.");
    });
  }

  return (
    <div className='login-page'>
      <div>
        <NavBar/>
      </div>
      <div className='login-form'>
     <form action="" method="post" >
        <h2 className='login-title'>Login</h2>
        <h5 className='login-register'>Don't have an account?<Link to='./register'><span>Register</span></Link></h5>
          <div className="container">
            <input type="email" placeholder="E-mail Address" name="uname" required />
            <input type="password" placeholder="Password" name="psw" required />
            {error && <><small style={{ color: 'red' }}>{error}</small><br /></>}<br />
            <button type="submit" value={loading ? 'Loading...' : 'Login'} onClick={handleLogin} disabled={loading} >Login</button>
          </div>
        </form>
        </div>
      </div>
  );
}

const useFormInput = initialValue => {
  const [value, setValue] = useState(initialValue);

  const handleChange = e => {
    setValue(e.target.value);
  }
  return {
    value,
    onChange: handleChange
  }
};
export default LoginPage;
