import React from 'react'
//import { FaUserCircle,FaUserFriends,FaHome,FaUsers} from 'react-icons/fa';
//import { FiLogOut} from 'react-icons/fi';
import '../LeftSide/LeftSide.css';
import { member } from '../../Data/member';

import { connect } from 'react-redux';
import { addFollowedMember, removeFollowedMember } from '../../actions/actions';

function MemberCard({ member, followedMembers, addFollowedMember, removeFollowedMember }) {
  const isFollowed = followedMembers.some((m) => m.id === member.id);

  const handleFollowClick = () => {
    if (isFollowed) {
      removeFollowedMember(member.id);
    } else {
    addFollowedMember(member);
    }
    };
    return (
      <div>
        <h3>{member.name}</h3>
        <p>{member.bio}</p>
        <button onClick={handleFollowClick}>{isFollowed ? 'Unfollow' : 'Follow'}</button>
      </div>
    );
  }

  function mapStateToProps(state) {
  return { followedMembers: state.followedMembers };
  }

  export default connect(mapStateToProps, { addFollowedMember, removeFollowedMember })(MemberCard);











