import './Login.css';
import NavBar from '../Home/NavBar';
//import React, { useState } from 'react';
//import axios from 'axios';
import { Link } from 'react-router-dom';
// import { setUserSession } from '../Utils/Common'
import logo from '../../images/logo.png'
import { FaEnvelope, FaLock, FaFacebook,FaGoogle,FaApple} from 'react-icons/fa';


const LoginPage = (props) => {
  /* const [loading, setLoading] = useState(false);
  const email = useFormInput('');
  const password = useFormInput('');
  const [error, setError] = useState(null);

  const handleLogin = () => {
    setError(null);
    setLoading(true);
    axios.post('', { email: email.value, password: password.value }).then(response => {
      setLoading(false);
      setUserSession(response.data.token, response.data.user);
      props.history.push('/userpage');
    }).catch(error => {
      setLoading(false);
      if (error.response.status === 401) setError(error.response.data.message);
      else setError("Something went wrong. Please try again later.");
    });
  } */

  return (
    <div className='login-page'>
      <div>
        <NavBar/>
      </div>
      <div className="login-card-container">
        <div className="login-card">
            <div className="login-card-logo">
                <img src={logo} alt="logo"/>
            </div>
            <div className="login-card-header">
                <h1>Sign In</h1>
                <div>Please login to use the platform</div>
            </div>
            <form className="login-card-form">
                <div className="form-item">
                <i><FaEnvelope /></i>
                 <input type="text" placeholder="Enter Email"></input>
                </div>
                <div className="form-item">
                <FaLock/>
                    <input type="password" placeholder="Enter Password"/>
                </div>
                <div className="form-item-other">
                    <div className="checkbox">
                        <input type="checkbox" id="rememberMeCheckbox" />
                        <label htmlFor="rememberMeCheckbox">Remember me</label>
                    </div>
                    <a href="/" className='login-card-frgt-pwd'>I forgot my password!</a>
                </div>
                <Link to ='/userpage'><button type="submit" className='login-btn'>Sign In</button></Link>
            </form>
            <div className="login-card-footer">
                Don't have an account? <Link to ='/register' className='new-acc'>Create a free account.</Link>
            </div>
        </div>
        <div className="login-card-social">
            <div>Other Sign-In Options</div>
            <div className="login-card-social-btns">
                <a href="/"><i></i><FaFacebook/></a>
                <a href="/"><FaGoogle/></a>
                <a href="/"><FaApple/></a>
            </div>
        </div>
    </div>
      </div>
  );
}

