import React, { useState, useEffect } from 'react';
import axios from 'axios';
import LeftSide from '../LeftSide/LeftSide';
import NewResources from '../NewResources/NewResources';
import AppBar from '../AppBar/AppBar';
import RightSide from '../RightSide/RightSide';
import { FaMinus , FaPen } from 'react-icons/fa';

import './profile.css'
import profileimg from '../../images/profile.webp'
import postpic from '../../images/postpic.webp'


const Profile = ({ userId }) => {
  const [resources, setResources] = useState([]);
  const [userInfo, setUserInfo] = useState({});

  useEffect(() => {
    const fetchUserInfo = async () => {
      const response = await axios.get(`/api/users/${userId}`);
      setUserInfo(response.data);
    };
    fetchUserInfo();
  }, []);

  useEffect(() => {

    const fetchResources = async () => {
      const response = await axios.get(`/api/resources/${userId}`);
      setResources(response.data);
    };
    fetchResources();
  }, [userId]);

  return (
    <div className='user-page'>
      <div className='user-header'><AppBar /></div>
      <div className='user-profile'>
        <div className='user-profile-info'>
        <img src={profileimg}></img>
        <p>Ilham<span>Abouelhouda</span></p>
        <p>abouelhoudailham@gmail.com</p>
        {/* 
        <img>{userInfo.image}</img>
        <p>First Name: {userInfo.firstname} <span>Last Name: {userInfo.lastname}</span></p>
        <p>Email: {userInfo.email}</p> */}
        </div>
        <button>Update</button>
        </div>
        <div className='user-profile-down'>
      <div className='user-info'>
        <div><LeftSide/></div>
        </div>
          <div className='user-resource'>
          <div><NewResources/></div>
          <div className='my-resource'>
            <div className='profile-resource'>
              {/* 
        <img>{userInfo.image}</img>
        <p>First Name: {userInfo.firstname} <span>Last Name: {userInfo.lastname}</span></p>
       */}
            <img src={profileimg}></img>
            <p>Ilham<span>  Abouelhouda</span></p>
            </div>


       {/*  {resources.map((resource) => (
          <div key={resource.id}> 
           <img src={resource.image} alt={resource.title} />
            <h2>{resource.title}</h2>
            <p>{resource.content}</p> 
          </div>
        ))} */} 

        <img src={postpic}></img>
            <h2>Title</h2>
            <p>Description</p>
      </div>

        </div>
        <div className='user-following'><RightSide/></div>
        </div>
        </div>

  );
};

export default Profile;
