import './FollowersCard.css'
import React, { Component } from 'react'
import FollowersService from '../Services/FollowersService'

class FollowersCard extends Component {
    constructor(props) {
        super(props)

        this.state = {
            followers: []
        }

        this.addFollower = this.addFollower.bind(this);
        this.deleteFollower = this.deleteFollower.bind(this);
    }

    deleteFollower(id) {
        FollowersService.deleteFollower(id).then(res => {
            this.setState({ followers: this.state.followers.filter(follower => follower.id !== id)});
        });
    }

    viewFollower(id) {
        this.props.history.push(`/view-follower/${id}`);
    }

    componentDidMount() {
        FollowersService.getFollowers().then(res => {
            this.setState({ followers: res.data});
        });
    }

    addFollower() {
        this.props.history.push('/add-follower/_add');
    }

    render() {
        return (
            <div className="FollowersCard">
                <h3>Who is following you</h3>

                { this.state.followers.map(follower => 
                    <div className="follower" key={follower.id}>
                        <div>
                            <img src={follower.img} alt="" className='followerImage' />
                            <div className="name">
                                <span>{follower.fullname}</span>
                            </div>
                        </div>
                        <div>
                            <button onClick={() => this.deleteFollower(follower.id)}>Delete</button>
                            <button onClick={() => this.viewFollower(follower.id)}>View</button>
                        </div>
                    </div>
                )}
            </div>
        )
    }
}

export default FollowersCard
