import './Login.css';
import NavBar from '../Home/NavBar';
import { Link } from 'react-router-dom';
import { useNavigate } from "react-router-dom";
import { useState } from 'react';
import { FaEnvelope, FaLock, FaFacebook, FaGoogle, FaApple, FaEyeSlash, FaEye } from 'react-icons/fa';

const LoginPage = () => {
  const user = useUser();
  const navigate = useNavigate();
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const handleTogglePassword = () => {
    setShowPassword(!showPassword);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Add your logic for form submission here
  };
  function sendLoginRequest() {
    const reqBody = {
      email: email,
      password: password,
    };

    fetch("api/auth/login", {
      headers: {
        "Content-Type": "application/json",
      },
      method: "post",
      body: JSON.stringify(reqBody),
    })
      .then((response) => {
        if (response.status === 200) return response.text();
        else if (response.status === 401 || response.status === 403) {
          setErrorMsg("Invalid username or password");
        } else {
          setErrorMsg(
            "Something went wrong, try again later or reach out to trevor@coderscampus.com"
          );
        }
      })
      .then((data) => {
        if (data) {
          user.setJwt(data);
          navigate("/register");
        }
      });
  }
  return (
    <div className='login-page'>
      <NavBar />
      <div className="login-card-container">
        <div className="login-card">
          <div className="login-card-header">
            <h1>Sign In</h1>
            <div>Please login to use the platform</div>
          </div>
          <form className="login-card-form" onSubmit={handleSubmit}>
            <div className="form-item">
              <i><FaEnvelope size={'1.3em'} color='#37018e' className='form-item-icon'/></i>
              <input type="email"  className="form-input"  placeholder="xxxxxx@gmail.com"
                value={username}
                onChange={(e) => setUsername(e.target.value)} />
            </div>
            <div className="form-item">
              <FaLock size={'1.3em'} color='#37018e' className='form-item-icon' />
              <input type={showPassword ? 'text' : 'password'} id='password' className="form-input" required />
              <i onClick={handleTogglePassword} className='form-item-icon-eye' placeholder="Type in your password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}>
                {showPassword ? <FaEye size={'1.3em'} color='#37018e'  /> : <FaEyeSlash size={'1.3em'} color='#37018e' />}
              </i>
            </div>
            <div className="form-item-other">
              <div className="checkbox">
                <input type="checkbox" id="rememberMeCheckbox" />
                <label htmlFor="rememberMeCheckbox">Remember me</label>
              </div>
              <a href="/" className='login-card-frgt-pwd'>I forgot my password!</a>
            </div>
            <Link to ='/userpage'><button type="submit" className='login-btn' /* onClick={() => sendLoginRequest()} */>Sign In</button></Link>
          </form>
          <div className="login-card-footer">
            Don't have an account? <Link to='/register' className='new-acc'>Create a free account.</Link>
          </div>
        </div>
        <div className="login-card-social">
          <div>Other Sign-In Options</div>
          <div className="login-card-social-btns">
            <a href="/"><FaFacebook size={'2em'} /></a>
            <a href="/"><FaGoogle size={'2em'} /></a>
            <a href="/"><FaApple size={'2em'} /></a>
          </div>
        </div>
      </div>
    </div>
  );
}

export default LoginPage;
