import React, { Component } from 'react'
import FollowersService from '../Services/FollowersService'

class ListEmployeeComponent extends Component {
    constructor(props) {
        super(props)

        this.state = {
                followers: []
        }
        this.addFollower = this.addFollowers.bind(this);
        this.deleteFollower = this.deleteFollower.bind(this);
    }

    deleteFollower(id){
        FollowersService.deleteFollower(id).then( res => {
            this.setState({followers: this.state.followers.filter(follower => follower.id !== id)});
        });
    }
    viewFollower(id){
        this.props.history.push(`/view-follower/${id}`);
    }
    componentDidMount(){
        FollowersService.getFollowers().then((res) => {
            this.setState({ followers: res.data});
        });
    }

    addEmployee(){
        this.props.history.push('/add-follower/_add');
    }

    render() {
        return (
            <div>
                 <h2 className="text-center">Followers</h2>

                 <br></br>
                 <div className = "row">
                        <table className = "table table-striped table-bordered">

                            <thead>
                                <tr>
                                    <th> Employee First Name</th>
                                    <th> Employee Last Name</th>
                                    <th> Employee Email Id</th>
                                    <th> Actions</th>
                                </tr>
                            </thead>
                            <tbody>
                                {
                                    this.state.employees.map(
                                        employee => 
                                        <tr key = {employee.id}>
                                             <td> { employee.firstName} </td>   
                                             <td> {employee.lastName}</td>
                                             <td> {employee.emailId}</td>
                                             <td>
                                                 <button onClick={ () => this.editEmployee(employee.id)} className="btn btn-info">Update </button>
                                                 <button style={{marginLeft: "10px"}} onClick={ () => this.deleteEmployee(employee.id)} className="btn btn-danger">Delete </button>
                                                 <button style={{marginLeft: "10px"}} onClick={ () => this.viewEmployee(employee.id)} className="btn btn-info">View </button>
                                             </td>
                                        </tr>
                                    )
                                }
                            </tbody>
                        </table>

                 </div>

            </div>
        )
    }
}

export default ListEmployeeComponent
