import axios from 'axios';

const FOLLOWERS_API_BASE_URL = "http://localhost:8080/api/v1/followers";

class FollowersService {

    getFollowers() {
        return axios.get(FOLLOWERS_API_BASE_URL);
    }

    createFollower(follower) {
        return axios.post(FOLLOWERS_API_BASE_URL, follower);
    }

    getFollowerById(followerId) {
        return axios.get(FOLLOWERS_API_BASE_URL + '/' + followerId);
    }

    updateFollower(follower, followerId) {
        return axios.put(FOLLOWERS_API_BASE_URL + '/' + followerId, follower);
    }

    deleteFollower(followerId) {
        return axios.delete(FOLLOWERS_API_BASE_URL + '/' + followerId);
    }
}

export default new FollowersService()
